import React, { useState } from 'react';
import styled from '@emotion/styled';
import { useNavigate } from 'react-router-dom';
import { Button, Modal, Paper, TextField, Typography } from '@mui/material';
import background from './signup-back.jpg';
import axios from 'axios';

const SignIn = () => {
  const [open, setOpen] = useState(false);
  const [data, setData] = useState({
    email:'',
    password:'',
  })
  const [registerMode, setRegisterMode] = useState(false);
  const navigate = useNavigate();

  const handleOpen = () => {
    setOpen((prevOpen) => !prevOpen);
    setRegisterMode(false);
  };

  const handleSignIn = async (event) => {
    event.preventDefault();
    try {
      const response = await axios.post('/api/auth/login', { email, password });
      localStorage.setItem('token', response.data.token);
      setOpen(false);
      navigate('/garden');
    } catch (error) {
      console.error('Error signing in:', error);
      alert('Invalid email or password');
    }
  };

  const handleRegister = async (event) => {
    event.preventDefault();
    try {
      await axios.post('/api/auth/register', { email, password });
      alert('Registration successful! Please sign in.');
      setEmail('');
      setPassword('');
      setRegisterMode(false);
    } catch (error) {
      console.error('Error registering:', error);
      alert('Error registering user');
    }
  };

  return (
    <AppContainer>
      <Button variant="contained" onClick={handleOpen} sx={{ bgcolor: '#ffffff', color: '#a9a9a9' }}>ログイン画面へ</Button>
      <Modal open={open} onClose={handleOpen}>
        <StyledPaper>
          <form className='form' onSubmit={registerMode ? handleRegister : handleSignIn}>
            <Typography variant={'h5'}>{registerMode ? 'Register' : 'Sign In'}</Typography>
            <TextField 
              label="Email address" 
              variant="standard" 
              className="text" 
              value={data.email}
              onChange={(e) => setData({...data,email:e.target.value})}
            />
            <TextField 
              label="Password" 
              variant="standard" 
              className="text" 
              type="password"
              value={data.password}
              onChange={(e) => setPassword({...data,password:e.target.value})}
            />
            <center>
              <Button type="submit" className={registerMode ? "signup btn" : "login btn"}>
                {registerMode ? 'Register' : 'Sign In'}
              </Button>
            </center>
            <center>
              <Button 
                className={registerMode ? "login btn" : "signup btn"} 
                onClick={() => setRegisterMode(!registerMode)}
              >
                {registerMode ? 'Sign In' : 'Register'}
              </Button>
            </center>
            <center>
              <Button variant="outlined" onClick={handleOpen}>close</Button>
            </center>
          </form>
        </StyledPaper>
      </Modal>
    </AppContainer>
  );
};

const AppContainer = styled.div`
  width: 100vw;
  height: 100vh;
  background-image: url(${background});
  background-size: cover;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const StyledPaper = styled(Paper)`
  display: flex;
  justify-content: center;
  align-items: center;
  width: 960px;
  height: 540px;
  .form {
    width: 60%;
    margin: 3rem;
    text-align: center;
  }
  .text {
    width: 100%;
    margin: 1rem 0;
  }
  .btn {
    width: 60%;
    color: white;
    text-align: center;
    margin: 1.5rem 0;
  }
  .login {
    background-color: #66cdaa;
  }
  .signup {
    background-color: #008080;
  }
`;

export default SignIn;
