import React, { useEffect, useState } from 'react';
import styled from '@emotion/styled';
import background from './garden_back.jpg';
import axios from 'axios';

import Tulip_Blooming from './FlowersIMGFile/Tulip/Blooming.png';
import Tulip_Half_Blooming from './FlowersIMGFile/Tulip/Half-blooming.png';
import Tulip_Withered from './FlowersIMGFile/Tulip/Withered.png';

const getAllTasks = async () => {
  try {
    const response = await axios.get('/api/v1/tasks');
    return response.data;
  } catch (error) {
    console.error('Error getting all tasks:', error);
    return [];
  }
};

const checkFlowerImage = (flowerImages) => {
  if (flowerImages === 'tulip') {
    return Tulip_Blooming;
  } else if (flowerImages === 'Tulip_Half_Blooming') {
    return Tulip_Half_Blooming;
  } else if (flowerImages === 'Tulip_Withered') {
    return Tulip_Withered;
  } else {
    return Tulip_Blooming;
  }
};

const Garden = () => {
  const [tasks, setTasks] = useState([]);
  const [tooltipIndex, setTooltipIndex] = useState(null);

  useEffect(() => {
    const fetchTasks = async () => {
      const data = await getAllTasks();
      setTasks(data);
    };
    fetchTasks();
  }, []);

  const toggleTooltip = (index) => {
    if (tooltipIndex === index) {
      setTooltipIndex(null);
    } else {
      setTooltipIndex(index);
    }
  };

  return (
    <GardenContainer>
      <Content>
        {tasks.map((task, index) => (
          <FlowerWrapper key={index}>
            <FlowerImage
              index={index}
              src={checkFlowerImage(task.flowerImages)}
              alt={task.flower}
              onClick={() => toggleTooltip(index)}
            />
            {tooltipIndex === index && (
              <Tooltip>
                <p>タイトル：{task.title}</p>
                <p>内容：{task.description}</p>
                <p>期限：{task.dueDate}</p>
              </Tooltip>
            )}
          </FlowerWrapper>
        ))}
      </Content>
    </GardenContainer>
  );
};

const GardenContainer = styled.div`
  width: 100vw;
  height: 100vh;
  background-image: url(${background});
  background-size: cover;
  background-position: center;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const Content = styled.div`
  text-align: center;
  color: white;
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  gap: 20px;
`;

const FlowerWrapper = styled.div`
  position: relative;
  display: inline-block;
  margin: 10px;
`;

const FlowerImage = styled.img`
  width: 150px;
  height: 150px;
  cursor: pointer; /* クリック可能にする */
`;

const Tooltip = styled.div`
  visibility: visible;
  width: 200px;
  background-color: black;
  color: #fff;
  text-align: center;
  border-radius: 6px;
  padding: 5px 0;
  position: absolute;
  z-index: 1;
  bottom: 125%; /* 画像の上にツールチップを配置 */
  left: 50%;
  margin-left: -100px;
  opacity: 1;
  transition: opacity 0.3s;
`;

export default Garden;
