import React, {useEffect, useState} from 'react';
import styled from '@emotion/styled';
import background from './garden_back.jpg'; // 修正した背景画像のパス
import axios from 'axios';

import Tulip_Blooming from './FlowersIMGFile/Tulip/Blooming.png';
import Tulip_Half_Blooming from './FlowersIMGFile/Tulip/Half-blooming.png';
import Tulip_Withered from './FlowersIMGFile/Tulip/Withered.png';

const getAllTasks = async () =>{
  try
  {
    let allTasks = await axios.get('/api/v1/tasks');
    let {data} = allTasks;
    return data;
  }
  catch(error){
    console.error('Error getting all tasks:', error);
  }
};
const checkFlowerImage = (flowerImages) => {
  if(flowerImages === 'tulip'){
    return Tulip_Blooming;
  }
  else if(flowerImages === 'Tulip_Half_Blooming'){
    return Tulip_Half_Blooming;
  }
  else if(flowerImages === 'Tulip_Withered'){
    return Tulip_Withered;
  }
  else{
    return Tulip_Blooming;
  }
}

const Garden = () => {
  let Tasks = getAllTasks();
  console.log(Tasks);
  const {_id, tiile, description, dueDate, flower, color, flowerImages} = Tasks;
  const flowerImage = checkFlowerImage(flowerImages);
  return (
    <GardenContainer>
      <Content>
        {/*<h1>Welcome to the Garden</h1>*/}
        {/* 他のコンテンツをここに追加 */}
        {Tasks.map((task, index) => (
          <FlowerImage
            key={index}
            index={index}
            src={flowerImage}
            alt={flower}
            />
        ))}
      </Content>
    </GardenContainer>
  );
};

const GardenContainer = styled.div`
  width: 100vw;
  height: 100vh;
  background-image: url(${background});
  background-size: cover;
  background-position: center;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const Content = styled.div`
  text-align: center;
  color: white;
  /* 他のスタイルをここに追加 */
`;
const FlowerImage = styled.img`
  position: absolute;
  top: ${({ index }) => 150+ Math.floor(index / 4) * 200}px; /* Adjust top position based on index */
  left: ${({ index }) => 230 + (index % 4) * 180}px; /* Adjust left position based on index */
  width: 150px;
  height: 150px;
`;

export default Garden;
