import React, {useEffect, useState} from 'react';
import styled from '@emotion/styled';
import background from './garden_back.jpg'; // 修正した背景画像のパス
import axios from 'axios';

import Tulip_Blooming from './FlowersIMGFile/Tulip/Blooming.png';
import Tulip_Half_Blooming from './FlowersIMGFile/Tulip/Half_blooming.png';
import Tulip_Withered from './FlowersIMGFile/Tulip/Withered.png';

const getAllTasks = async () =>{
  try
  {
    let allTasks = await axios.get('/api/v1/task');
    let {data} = allTasks;
    console.log(data);  
    console.log(Tulip_Blooming, Tulip_Half_Blooming,Tulip_Withered);
  }
  catch(error){
    console.error('Error getting all tasks:', error);
  }
};

const Garden = () => {
  getAllTasks()

  return (
    <GardenContainer>
      <Content>
        {/*<h1>Welcome to the Garden</h1>*/}
        {/* 他のコンテンツをここに追加 */}
      </Content>
    </GardenContainer>
  );
};

const GardenContainer = styled.div`
  width: 100vw;
  height: 100vh;
  background-image: url(${background});
  background-size: cover;
  background-position: center;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const Content = styled.div`
  text-align: center;
  color: white;
  /* 他のスタイルをここに追加 */
`;

export default Garden;
